#include <gmp.h>
#include <stdio.h>

int
main( int argc,char **argv )
{
    mpz_t a,b,c,res;

    mpz_init_set_str( a, "2" , 10 );
    mpz_init_set_str( b, "100000000000000000000000" , 10 );
    mpz_init_set_str( c, "" , 10 );
    mpz_init( res );

    mpz_pow_powm( res, a,b,c);

    printf( "%s\n", mpz_get_str( NULL,10,a ));
    printf( "%s\n", mpz_get_str( NULL,10,b ));
    printf( "%s\n", mpz_get_str( NULL,10,c ));    
    printf( "%s\n", mpz_get_str( NULL,10,res ) );
}
